static int ius006_WriteRegister8(struct i2c_client *client, u16 addr, u8 val)
{
    int err;
    struct i2c_msg msg;
    unsigned char data[3];
    int retry = 0;

    if (!client->adapter)
        return -ENODEV;

    data[0] = (u8) (addr >> 8);;
    data[1] = (u8) (addr & 0xff);
    data[2] = val;

    msg.addr = client->addr;
    msg.flags = 0;
    msg.len = 3;
    msg.buf = data;

    do {
        err = i2c_transfer(client->adapter, &msg, 1);
        if (err == 1)
            return 0;
        retry++;
        pr_err("ius006: i2c transfer failed, retrying %x %x\n",
                addr, val);
        msleep(3);
    } while (retry <= IUS006_MAX_RETRIES);

    return err;
}

static int ius006_WriteRegister16(struct i2c_client *client, u16 addr, u16 val)
{
    int err;
    struct i2c_msg msg;
    unsigned char data[4];
    int retry = 0;

    if (!client->adapter)
        return -ENODEV;

    data[0] = (u8) (addr >> 8);;
    data[1] = (u8) (addr & 0xff);
    
    if (addr <= 0x3FF) // usr dir Big Endian
    {
        data[2] = (u8)(val >> 8);        
        data[3] = (u8)(val & 0x00FF);      
    }
    else //Little Endian
    {
        data[2] = (u8)(val & 0x00FF);        
        data[3] = (u8)(val >> 8);                  
    }


    msg.addr = client->addr;
    msg.flags = 0;
    msg.len = 4;
    msg.buf = data;

    do {
        err = i2c_transfer(client->adapter, &msg, 1);
        if (err == 1)
            return 0;
        retry++;
        pr_err("ius006: i2c transfer failed, retrying %x %x\n",
                addr, val);
        msleep(3);
    } while (retry <= IUS006_MAX_RETRIES);

    return err;
}

static int ius006_WriteRegister32(struct i2c_client *client, u16 addr, u32 val)
{
    int err;
    struct i2c_msg msg;
    unsigned char data[6];
    int retry = 0;

    if (!client->adapter)
        return -ENODEV;

    data[0] = (u8) (addr >> 8);;
    data[1] = (u8) (addr & 0xff);
    
    if (addr <=  0x3FF) //usr dir Big Endian
    {
        data[2] = (u8)(val >> 24);
        data[3] = (u8)((val >> 16) & 0x00FF);
        data[4] = (u8)((val >> 8) & 0x0000FF);
        data[5] = (u8)(val & 0x000000FF);
    }
    else // Little Endian
    {
        data[5] = (u8)(val >> 24);
        data[4] = (u8)((val >> 16) & 0x00FF);
        data[3] = (u8)((val >> 8) & 0x0000FF);
        data[2] = (u8)(val & 0x000000FF);
    }


    msg.addr = client->addr;
    msg.flags = 0;
    msg.len = 6;
    msg.buf = data;

    do {
        err = i2c_transfer(client->adapter, &msg, 1);
        if (err == 1)
            return 0;
        retry++;
        pr_err("ius006: i2c transfer failed, retrying %x %x\n",
                addr, val);
        msleep(3);
    } while (retry <= IUS006_MAX_RETRIES);

    return err;
}

static int ius006_WriteRegisterSequential(struct i2c_client *client, u16 addr, u8 *val, u32 size)
{
    int err;
    u32 i = 0;

    if (!client->adapter)
        return -ENODEV;
        
    for(i = 0; i < size; i ++) 
    {
        err   = ius006_WriteRegister8(client, addr + i, *(val + i));
        if(err != 0)
        {
            return err;
        }
    }
    return 0;
}

static int ius006_read8(struct i2c_client *client, u16 addr, u8 *val)
{
    int err;
    struct i2c_msg msg[2];
    unsigned char data[3];

    if (!client->adapter)
        return -ENODEV;

    msg[0].addr = client->addr;
    msg[0].flags = 0;
    msg[0].len = 2;
    msg[0].buf = data;

    /* high byte goes out first */
    data[0] = (u8) (addr >> 8);;
    data[1] = (u8) (addr & 0xff);

    msg[1].addr = client->addr;
    msg[1].flags = I2C_M_RD;
    msg[1].len = 1;
    msg[1].buf = data + 2;

    err = i2c_transfer(client->adapter, msg, 2);

    if (err != 2) {
        printk("%s read reg 0x%04x error\n",__func__, addr);
        return -EINVAL;
    }
    *val=data[2];
    return 0;
}
static int ius006_read16(struct i2c_client *client, u16 addr, u16 *val)
{
    int err;
    struct i2c_msg msg[2];
    unsigned char data[4];

    if (!client->adapter)
        return -ENODEV;

    msg[0].addr = client->addr;
    msg[0].flags = 0;
    msg[0].len = 2;
    msg[0].buf = data;

    /* high byte goes out first */
    data[0] = (u8) (addr >> 8);;
    data[1] = (u8) (addr & 0xff);

    msg[1].addr = client->addr;
    msg[1].flags = I2C_M_RD;
    msg[1].len = 2;
    msg[1].buf = data + 2;

    err = i2c_transfer(client->adapter, msg, 2);

    if (err != 2) {
        printk("%s read reg 0x%04x error\n",__func__, addr);
        return -EINVAL;
    }
    if(addr <= 0x3FF){
        *val=data[2]<<8 | data[3];
    }else{
        *val=data[2] | data[3]<<8;
    }

    return 0;
}
static int ius006_read32(struct i2c_client *client, u16 addr, u32 *val)
{
    int err;
    struct i2c_msg msg[2];
    unsigned char data[6];

    if (!client->adapter)
        return -ENODEV;

    msg[0].addr = client->addr;
    msg[0].flags = 0;
    msg[0].len = 2;
    msg[0].buf = data;

    /* high byte goes out first */
    data[0] = (u8) (addr >> 8);;
    data[1] = (u8) (addr & 0xff);

    msg[1].addr = client->addr;
    msg[1].flags = I2C_M_RD;
    msg[1].len = 4;
    msg[1].buf = data + 2;

    err = i2c_transfer(client->adapter, msg, 2);

    if (err != 2) {
        printk("%s read reg 0x%04x error\n",__func__, addr);
        return -EINVAL;
    }
    if(addr <= 0x3FF){
        *val=data[2]<<24 | data[3]<<16 | data[4]<<8 | data[5];
    }else{
        *val=data[2] | data[3]<<8 | data[4]<<16 | data[5]<<24;
    }

    return 0;
}

static int ius006_WriteI2CSequence(struct i2c_client *client, struct ius006_reg *pSequence)
{
    u32 index = 0;
    int status  = 0;
    for(;;)
    {
        u16 addr = pSequence->addr;
        u32 val = pSequence->val;
        
        if (addr == SEQUENCE_END)
        {
            break;
        }
        
        switch(pSequence->size)
        {
        case 1:
            status = ius006_WriteRegister8(client, addr, val);
            if (status != 0)
            {
                return status;
            }
            break;
        case 2:
            status = ius006_WriteRegister16(client, addr, val);
            if (status != 0)
            {
                return status;
            }
            break;
        case 4:
            status = ius006_WriteRegister32(client, addr, val);
            if (status != 0)
            {
                return status;
            }
            break;
        default:
            printk("%s, error size, index=%d %d\n", __func__, index, pSequence->size); 
            return status;
        }
        index ++;
//        msleep(1); // need?
        pSequence++;
    }

    return 0;
}

static int ius006_InitialSetting_1(struct i2c_client *client)
{       
    int err     = 0;
    u8 data   = 0;
    int retry = 0;

    printk("Enter %s\n", __func__); 

    do {   
        err = ius006_read8(client,0x00F8,&data);
    }while(err != 0);

    do {        
        msleep(10);
        err =  ius006_read8(client, 0xF8, &data);
        if(err != 0)
        {
            printk("%s: read initial status register error, date=%02x! \n", __func__, data); 
            return err;
        }
        else
        {   
            printk("%s: first initial status register: %02x\n", __func__, data); 
            retry++;
        }
    }while(((data & 0x01) == 0) && retry < INTSTS0_MAX_RETRIES);
    if (retry >= INTSTS0_MAX_RETRIES){
        printk("read INTSTS0 over 500 times\n");
        return -EAGAIN;
    }

    ius006_WriteRegister8(client, 0xFC, 0x01);
    
    err = ius006_WriteI2CSequence(client, g_InitialSetting_1);
    if (err != 0)
    {
        return err;
    }
    udelay(300);

    do {        
        msleep(10);
        err =  ius006_read8(client, 0xF8, &data);
        if(err != 0)
        {
            printk("%s: sencond read initial status register error data=%02x!\n", __func__, data);
            return err;
        }
        else
        {   
            printk("%s: sencond initial status register: %02x\n", __func__, data); 
            retry++;
        }
    }while(((data & 0x01) == 0) && retry < INTSTS0_MAX_RETRIES);
    if (retry >= INTSTS0_MAX_RETRIES){
        printk("read INTSTS0 over 500 times\n");
        return -EAGAIN;
    }

    ius006_WriteRegister8(client, 0xFC, 0x01);

    return err;
}

