static int ius006_InitialAF(struct i2c_client *client)
{
    int status = 0;
    u8 data    = 0;
    u16 value  = 0;

    u32   size  = sizeof(g_AutoFocusDriver);
//    printk("%s: driver size %d\n", __func__, size);
//AF driver transfer
    status = ius006_WriteRegisterSequential(client, 0xc000, g_AutoFocusDriver, size);
    if(status)
    {
        printk("Leave %s: write 0xc000 faile\n", __func__);
        return status;
    }
//checksum
    status = ius006_WriteRegister16(client, 0x485E, 0x8D27);
    if(status)
    {
        printk("Leave %s: write 0x485E faile,status=%d\n", __func__,status);
        return status;
    }
    status = ius006_WriteRegister16(client, 0x485C, 0x028c);
    if(status)
    {
        printk("Leave %s: write 0x485C faile\n", __func__);
        return status;
    }
//initialization process
    status   = ius006_read8(client, 0x000A, &data);
    if(status)
    {
        printk("Leave %s: read AF_STATE faile, %08x\n", __func__, status);
        
        return status;
    }

    data |= 0x01;

    status = ius006_WriteRegister8(client, 0x000A, data);
    if(status)
    {
        printk("Leave %s: write AF_EXT faile\n", __func__);
        return status;
    }
    do
    {
        status   = ius006_read8(client, 0x6D76, &data);
        if(status)
        {
            printk("Leave %s: read AF_STATE faile, %08x\n", __func__, status);            
            return status;
        }
        printk("%s: read AF_STATE, %02x\n", __func__,data); 
        if(data==0xFF){
            printk("af driver loading failed\n");
            return -1;
        }
    }while(data!=0x03);

    do
    {
        status   = ius006_read8(client, 0x000A, &data);
        if(status)
        {
            printk("Leave %s: read AF_EXT faile, %08x\n", __func__, status);            
            return status;
        }
        printk("%s: AF_EXT %02x\n", __func__, data);
    } while((data & 0x02) == 0);

    status   = ius006_read16(client, 0x485E, &value);
    if(value==0xFFFF)
        printk("AF driver loading is successful\n");
    else
        printk("Af driver loading error\n");
    return status;
}

static int ius006_flip(struct i2c_client *client)
{       
    int ret = 0;    
    u32 odm_rsvd[8];
    unsigned int model_id;
    unsigned int device_id;
#define MODEL_EVT 0xc
#define DEVICE_NBX02 0x0
#define DEVICE_NBX03 0x1
//    printk("Enter %s\n", __func__); 

    ret = tegra_fuse_read(ODM_RSVD, odm_rsvd, sizeof(odm_rsvd));
    if(ret!=0)return 0;
    model_id = odm_rsvd[0]&0xf;
    device_id = (odm_rsvd[0] >> 4) & 0x07;

    printk("Entet %s, model_id=%#x\n", __func__,model_id); 
    if((device_id == DEVICE_NBX02) || ((device_id == DEVICE_NBX03) && (model_id == MODEL_EVT))){
        ius006_WriteRegister8(client, 0x001F, 3);
        ius006_WriteRegister8(client, 0x0020, 3);
        ius006_WriteRegister8(client, 0x0021, 3);
    }else{
        ius006_WriteRegister8(client, 0x001F, 0);
        ius006_WriteRegister8(client, 0x0020, 0);
        ius006_WriteRegister8(client, 0x0021, 0);
    }

    return model_id;
}

static int ius006_set_init_dev(struct ius006_info *info)
{
    int status = 0;
    printk("Enter %s\n", __func__);

    status  = ius006_InitialSetting_1(info->i2c_client);
    if(status != 0)
    {
        return status;
    }
    status  = ius006_read_otpdata(info->i2c_client);
    if(status != 0)
    {

        printk("ius006 read otp data error\n");
        return status;
    }
    status  = ius006_InitialSetting_2(info->i2c_client);
    if(status != 0)
    {
        printk("SensorIus006_InitialSetting_2 error\n");
        return status;
    }
    ius006_flip(info->i2c_client);
    if(status)
    {
        printk("ius006 flip  error\n");
    }
    status  = ius006_InitialSetting_3(info->i2c_client);
    if(status != 0)
    {
        printk("ius006_InitialSetting_3 error\n");
        return status;
    }
#ifdef CONFIG_MACH_NBX03
    gpio_set_value(GPIO_PIN_5M_PWRDWN, 1);
#else
    tc35892_set_standby();
#endif
    mdelay(32);
    status  = ius006_InitialSetting_4(info->i2c_client);
    if(status != 0)
    {
        printk("ius006_InitialSetting_4 error\n");
        return status;
    }
    
    status  = ius006_InitialSetting_5(info->i2c_client);
    if(status != 0)
    {
        printk("ius006_InitialSetting_5 error\n");
        return status;
    }

    status  = ius006_InitialAF(info->i2c_client);
    if(status != 0)
    {
        return status;
    }    

    return 0;
}

