static int ius006_set_mode(struct ius006_info *info, struct ius006_mode *mode)
{
    u8 data;
    int retry = 0;
    int status = 0;
    int current_mode = mode->current_mode;
    int next_mode = mode->next_mode;

    printk("%s:  current mode =%d,next mode =%d\n", __func__,current_mode,next_mode);

    if(next_mode == MODE_MONITORING){
        ius006_WriteRegister8(info->i2c_client, REG_INTCLR0, 3);

        ius006_WriteRegister16(info->i2c_client, REG_HSIZE_MONI, preview_width);
        ius006_WriteRegister16(info->i2c_client, REG_VSIZE_MONI, preview_height);
        ius006_WriteRegister8(info->i2c_client, REG_FPSTYPE_MONI, fps_value);

        if((current_mode == MODE_MONITORING) || (current_mode == MODE_MOVIE)){
            ius006_WriteRegister8(info->i2c_client, REG_MONI_REFLESH, 1);
        }else{
            ius006_WriteRegister8(info->i2c_client, REG_MODESEL, MODE_MONITORING);
        }
        do{
            msleep(10);
            status = ius006_read8(info->i2c_client, REG_INTSTS0, &data);
            if(status == 0)
            {
                printk("ius006_SetMode: I2C read %02x\n", data);
                retry++;
            }
            else
            {
                printk("ius006_SetMode: I2C read error\n");
                return status;
            }
        }while(((data & 0x2) == 0) && retry < INTSTS0_MAX_RETRIES);
        if (retry >= INTSTS0_MAX_RETRIES){
            printk("read INTSTS0 over 500 times\n");
            return -EAGAIN;
        }
        ius006_WriteRegister8(info->i2c_client, REG_INTCLR0, 2);

        ius006_read8(info->i2c_client, 0x0004, &data);
        printk("REG( PICT_OUT_INFO_NOW) = %#x\n",data);
        printk("Monitoring finish\n");

    }else if(next_mode == MODE_HALFRELEASE){
        if(current_mode == MODE_HALFRELEASE){
            ius006_WriteRegister8(info->i2c_client, REG_AF_RESTART_F, 0x01);
        }else{
            ius006_WriteRegister8(info->i2c_client, REG_INTCLR0,0x12);
            ius006_WriteRegister8(info->i2c_client, REG_MODESEL, MODE_HALFRELEASE);
            do{
                msleep(10);
                status = ius006_read8(info->i2c_client, REG_INTSTS0, &data);
                if(status == 0)
                {
                    printk("ius006_SetMode: I2C read %02x\n", data);
                    retry++;
                }
                else
                {
                    printk("ius006_SetMode: I2C read error\n");
                    return status;
                }
            }while(((data & 0x2) == 0) && retry < INTSTS0_MAX_RETRIES);
            if (retry >= INTSTS0_MAX_RETRIES){
                printk("read INTSTS0 over 500 times\n");
                return -EAGAIN;
            }

            ius006_WriteRegister8(info->i2c_client, REG_INTCLR0,0x02);
        }
        ius006_read8(info->i2c_client, 0x0004, &data);
        printk("REG( PICT_OUT_INFO_NOW) = %#x\n",data);
        printk("Halfrelease finish\n");
    }else if(next_mode == MODE_CAPTURE){
        ius006_WriteRegister8(info->i2c_client, REG_INTCLR0, 2);

        ius006_WriteRegister16(info->i2c_client, REG_HSIZE_CAP, capture_width);
        ius006_WriteRegister16(info->i2c_client, REG_VSIZE_CAP, capture_height);
        ius006_WriteRegister8(info->i2c_client, REG_FPSTYPE_CAP, 3);

        ius006_WriteRegister8(info->i2c_client, REG_MODESEL, MODE_CAPTURE);
        do{
            msleep(10);
            status = ius006_read8(info->i2c_client, REG_INTSTS0, &data);
            if(status == 0)
            {
                printk("ius006_SetMode: I2C read %02x\n", data);
                retry++;
            }
            else
            {
                printk("ius006_SetMode: I2C read error\n");
                return status;
            }
        }while(((data & 0x2) == 0) && retry < INTSTS0_MAX_RETRIES);
        if (retry >= INTSTS0_MAX_RETRIES){
            printk("read INTSTS0 over 500 times\n");
            return -EAGAIN;
        }
        ius006_WriteRegister8(info->i2c_client, REG_INTCLR0, 2);
        ius006_read8(info->i2c_client, 0x0004, &data);
        printk("REG( PICT_OUT_INFO_NOW) = %#x\n",data);
        printk("capture finish\n");

    }else if(next_mode == MODE_MOVIE){
        ius006_WriteRegister8(info->i2c_client, REG_INTCLR0, 2);

        ius006_WriteRegister16(info->i2c_client, REG_HSIZE_MONI, movie_width);
        ius006_WriteRegister16(info->i2c_client, REG_VSIZE_MONI, movie_height);
        ius006_WriteRegister8(info->i2c_client, REG_FPSTYPE_MONI, fps_value);

        if((current_mode == MODE_MONITORING) || (current_mode == MODE_MOVIE)){
            ius006_WriteRegister8(info->i2c_client, REG_MONI_REFLESH, 1);
        }else{
            ius006_WriteRegister8(info->i2c_client, REG_MODESEL, MODE_MONITORING);
        }
        do{
            msleep(10);
            status = ius006_read8(info->i2c_client, REG_INTSTS0, &data);
            if(status == 0)
            {
                printk("ius006_SetMode: I2C read %02x\n", data);
                retry++;
            }
            else
            {
                printk("ius006_SetMode: I2C read error\n");
                return status;
            }
        }while(((data & 0x2) == 0) && retry < INTSTS0_MAX_RETRIES);
        if (retry >= INTSTS0_MAX_RETRIES){
            printk("read INTSTS0 over 500 times\n");
            return -EAGAIN;
        }
        ius006_WriteRegister8(info->i2c_client, REG_INTCLR0, 2);

        ius006_read8(info->i2c_client, 0x0004, &data);
        printk("REG( PICT_OUT_INFO_NOW) = %#x\n",data);
        printk("movie finish\n");

    }
    return status;

}

